		let masonry = [];

		content.map((item, index) => {
			masonry.push( <div key={index}>sup</div> )
		})

		console.log(content)
		// console.log(masonry)

		return masonry;
	}

	return (
		<div><GenerateContent /></div>
	)
}

export default Archive;
