import React, { useState } from 'react';
import arrow from '../../assets/images/arrow.png';
import styles from './mouse.module.scss';
import classNames from 'classnames';

const Mouse = (props) => {
	const [ showMouse, setShowMouse ] = useState(true);
	const [ mouse, setMouse ] = useState({x: 0, y: 0});
	// console.log(props.mouseX)

	const handleMouse = (e) => {
		console.log(e.clientX);
		setMouse({x: e.clientX - window.innerWidth - 95, y: e.clientY});
	}

	return (
		<div onMouseMove={ (e) => handleMouse(e) } style={{position: 'absolute', width: '50%', height: '100vh', zIndex: '5'}}>
			<img src={arrow} style={{position: 'absolute', left: mouse.x}} className={classNames(styles.mouseImage, {[styles.showMouse] : showMouse})} width="190" height="23" alt="mouse"/>
		</div>
	)
}

export default Mouse;
