import React, { useState } from 'react';
import arrow from '../../assets/images/arrow.png';
import styles from './mouse.module.scss';
import classNames from 'classnames';

const Mouse = (props) => {
	const [ showMouse, setShowMouse ] = useState(true);
	// console.log(props.mouseX)

	const handleMouse = (e) => {
		console.log(e.clientX);
	}

	return (
		<div onMouseMove={ (e) => handleMouse(e) } style={{position: 'absolute', width: '100%', height: '100vh'}}>
			<img src={arrow} style={{position: 'absolute', left: props.mouseX}} className={classNames(styles.mouseImage, {[styles.showMouse] : showMouse})} width="190" height="23" alt="mouse"/>
		</div>
	)
}

export default Mouse;
