import React, { useEffect, useState } from 'react';
import styles from './randomizer.module.scss';

const Randomizer = (props) => {
	let chance;

	const [ show, setShow ] = useState(false);

	useEffect(() => {
		chance = Math.floor(Math.random() * 10);
		// console.log(chance)
	},[])

	const handleChance = () => {
		switch (chance) {
			case 0:
				console.log('its 0')
				break;
			case 1:
				console.log('its 1')
				break;
			default:
				console.log('chances man')
		}
	}

	const showProject = () => {
		setShow(true);
	}

	return (
		<React.Fragment>
			<div className={styles.randomizer} onClick={showProject}/>
			{ show && <div>hello sis</div> }
		</React.Fragment>
	)
}

export default Randomizer;
