	const [ show, setShow ] = useState(false);

	return (
		<React.Fragment>
			<div className={styles.randomizer} onClick={() => setShow(true)}/>
			{ show && 
				<div className={styles.archiveContainer}>
					<div className={styles.archiveTitle}>
						<button onClick={() => setShow(false)}><img src={back} alt="back to main" width="31" height="17"/></button>
						<p>An archive of side projects to keep it alive.</p>
					</div>
					<img src={tt_1} alt="totallytubular" />
					<img src={tt_2} alt="totallytubular" />
					<img src={tt_3} alt="totallytubular" />

				</div> }
		</React.Fragment>
	)
}

export default Randomizer;
