import React, { useState, useEffect } from 'react'; 
import Masonry from 'react-masonry-css'
import styles from './archive.module.scss';

var Airtable = require('airtable');

const Archive = () => {

	const [ content, setContent ] = useState([]);

	var allContent = [];

	const retrieveContent = () => {

		var base = new Airtable({apiKey: 'keywJEKrRFRrPEmMI'}).base('appS0UCDSYim1dOsM');
		base('Work').select().eachPage(function page(records, fetchNextPage) {

		    records.forEach(function(record) {
		    	let type = record.get('type');
		    	if (type === "archive") {
		    		allContent.push(record);
		    	}
		    });

		    fetchNextPage();

		}, function done(err) {
		    if (err) { console.error(err); return; }
		    setContent(allContent);
		});
	}	

	useEffect(() => {
		retrieveContent();
	}, []);


	const GenerateContent = () => {
		let masonry = [];

		content.map((item, index) => {
			// console.log(item.fields.attachments[0].url)
			if (item.fields.attachments) {
				let src = item.fields.attachments[0].url;
				masonry.push( <div><img key={index} src={src} width="300" height="auto"/></div> )
			}

		})

		return masonry;
	}

	return (
		<Masonry
		  breakpointCols={3}
		  className={styles.grid}
		  columnClassName={styles.column}>
  			{/*<GenerateContent />*/}
  			<div>hi</div>
  			<div>hi</div>
  			<div>hi</div>
  			<div>hi</div>
  		</Masonry>
	)
}

export default Archive;
