import React, { useEffect, useState } from 'react';
import styles from './border.module.scss';
import classNames from 'classnames';

const Border = (props) => {

	const [ width, setWidth ] = useState();

	useEffect( () => {

		// const items = Array.prototype.slice.call( borderEl.current.childNodes )

		// for (var i = 0; i < items.length; i++) {
		// 	animTiming(items[i])
		// }

		// setWidth(window.innerWidth);

		// window.addEventListener('resize', function(event){
		//   setWidth(window.innerWidth);
		// });

		// return (() => {
		// 	window.removeEventListener('resize', function(event) {
		// 		//
		// 	})
		// });

	})

	const animTiming = (item) => {
			setTimeout ( function (){
		      item.classList.add(`${styles.animate}`);
		    });
		}


	const borderEl = React.createRef();
	let flexClass;

	const createText = (count) => {
		let array = [];
		for (var i = 0; i < count; i++) {
			array.push(<div key={i} className={classNames("text", styles.borderText, flexClass)}>NOT JUST ANOTHER WORDPRESS SITE &nbsp;</div>)
		}

		return array;
	}

	const handleResponsive = () => {
		let text;
		if (width > 1600) {
			flexClass = `${styles.xl}`
			text = createText(6);
		} else if (width > 1200 && width < 1600) {
			flexClass = `${styles.l}`
			text = createText(4);
		}

		return text;
	}

	return (
		<div className={styles.border} >
			{/*{handleResponsive()}*/}
			<div className={classNames("text", styles.borderText, styles.animate)}>
				NOT JUST ANOTHER WORDPRESS SITE NOT JUST ANOTHER WORDPRESS SITE NOT JUST ANOTHER WORDPRESS SITE NOT JUST ANOTHER WORDPRESS SITE NOT JUST ANOTHER WORDPRESS SITE NOT JUST ANOTHER WORDPRESS SITE &nbsp;
			</div>
		</div>
	)
}

export default Border;
