import React, { useEffect } from 'react';
import styles from './border.module.scss';
import classNames from 'classnames';

const Border = (props) => {

	useEffect( () => {
		const items = Array.prototype.slice.call( borderEl.current.childNodes )

		for (var i = 0; i < items.length; i++) {
			animTiming(items[i])
		}
	})

	const animTiming = (item) => {
		setTimeout ( function (){
	      item.classList.add(`${styles.animate}`);
	    });
	}

	const borderEl = React.createRef();

	const createText = (count) => {
		let array = [];
		for (var i = 0; i < count; i++) {
			array.push(<div className={classNames("text", styles.borderText)}>NOT JUST ANOTHER WORDPRESS SITE &nbsp;</div>)
		}

		return array;
	}

	const handleResponsive = () => {
		if (window.innerWidth > 1600) {
			createText(6);
		}
	}

	return (
		<div className={styles.border} ref={borderEl}>
			{handleResponsive()}
		</div>
	)
}

export default Border;
