import React from 'react';
import arrow from '../../assets/images/arrow.png';
import styles from './mouse.module.scss';

const Mouse = (props) => {
	return (
		<img src={arrow} style={{position: 'absolute', left: props.mouseX}} className={classNames(styles.mouseImage, {[styles.showMouse] : showMouse})} width="190" height="23" alt="mouse"/>
	)
}

export default Mouse;
